'use strict';

const BigNumber = require('bignumber.js');

const factorial = (n) => {
 let prod = new BigNumber(1);
 for (let i=n;i>0;i=i-1) {
  prod = prod.times(i.toString());
 }
 return prod;
}

const f = factorial(100).toString();

console.log( f.substring(0,f.indexOf("e+")).replace(".","").split("").map(el=>parseInt(el)).reduce((a,b)=>a+b) );
