"use strict";

function isPalendrome6(number) {
  const string = number.toString();
  const half1 = string.substring(0,3);
  const half2 = string.substring(3,6).split("").reverse().join("");
  if (half1===half2) { return true; } else { return false; }

}

const n = 910;
const m = 990;

var p = [];
for (var i = n, j = m; i < 1000; i++) {
  while (j < 1000) {
    let k = i*j;
    if (isPalendrome6(k)) {
      p.push([i,j,k])
    }
    j++
  }
  j=m
}
p[p.length-1].forEach(function(el){console.log(el)})
